package com.collectionFrameworkQues;

import java.util.*;
public class ReverseListOfString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		List<String> name=new ArrayList<>();
		name.add("Apple");
		name.add("Banana");
		name.add("Grapes");
		name.add("Gauvava");
		name.add("Mangoes");
		System.out.println(name);
		System.out.println(name.reversed());
	}

}
